#!/bin/bash

# nodejs static server
port="${1:-3000}"
open "http://localhost:${port}/"
echo "Static file server running at => http://localhost:${port}"
echo "CTRL + C to shutdown"
node -e 'var http=require("http"),url=require("url"),path=require("path"),fs=require("fs"),port=process.argv[1]||3e3,backName="<<",log=function(e,r){var t=200===r?"\x1b[32m":"\x1b[31m";console.log("\x1b[36m",e.method,t,r,"\x1b[0m",e.url)},errorResponse=function(e,r,t,n){var o={"Content-Type":"text/plain"},a=404===e?"404 Not Found\n":n+"\n";log(r,e),t.writeHead(e,o),t.write(a),t.end()},normalResponse=function(e,r,t,n){var o={"Access-Control-Allow-Origin":"*"};log(e,200),r.writeHead(200,o),r.write(t,n),r.end()};http.createServer(function(e,r){var t=url.parse(e.url).pathname,n=path.join(process.cwd(),t);fs.exists(n,function(t){if(!t)return void errorResponse(404,e,r);var o=fs.statSync(n).isDirectory();if(o)fs.readdir(n,function(t,o){if(t)return errorResponse(500,e,r,t);var a="<html><head><style>body{margin:0;}.content{list-style:none;padding:10px;}.row{height:30px;line-height:30px}.row:hover{background-color:#f1f1f1}</style></head><body><ul class=\"content\">",s=function(r,t){t=t||r;var n="<li class=\"row\">";return n+="<a href=\""+e.url+t+"\">",n+=r,n+="</a></li>"};a+="/"===e.url?"":s(backName,"..");for(var i=0;i<o.length;i++){var l=o[i];l+=fs.statSync(n+l).isDirectory()?"/":"",a+=s(l)}return a+="</ul></body></html>",normalResponse(e,r,a)});else{var a=fs.createReadStream(n,{flags:"r",encoding:"utf8",fd:null,mode:438,autoClose:!0});a.on("open",function(){var e=path.basename(n);r.setHeader("Content-disposition","attachment; filename="+e),a.pipe(r)}),a.on("error",function(t){errorResponse(500,e,r,t)})}})}).listen(parseInt(port,10));' "$port"
